import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn, MDBCard, MDBCardBody } from 'mdbreact';
import './login.css'; 
import { sigIn } from '../../action/loginAction';   
import { connect } from 'react-redux';

class Login extends React.Component{
    constructor(props){
        super(props)
        this.state={
            username:'',
            password:'',
        }
    }

    inputChnage = (event) =>{
        const { name, value } =  event.target;
        this.setState({ [name]: value});


    }
    buttonSubmit = (event) => {
        event.preventDefault();
        let { username, password } = this.state
        if(!username || !password){
            alert("username should not be blank")
            return;
        }else{
            this.props.sigIn(this.state)
        }
    }
    render(){
        let { username, password } =  this.state;
        return(
            <MDBContainer>
            <MDBRow>
              <MDBCol md="6">
                <MDBCard>
                  <MDBCardBody>
                    <form>
                      <p className="h4 text-center py-4">Login</p>
                      <div className="grey-text">
                        <MDBInput
                         name="username"
                         value={username}
                          label="Your name"
                          icon="user"
                          group
                          type="text"
                          validate
                          error="wrong"
                          success="right"
                          onChange={(event) => this.inputChnage(event)}
                        />
                        <MDBInput
                            name="password"
                            value={password}
                          label="Your password"
                          icon="lock"
                          group
                          type="password"
                          validate
                          onChange={(event) => this.inputChnage(event)}
                        />
                      </div>
                      <div className="text-center py-4 mt-3">
                        <MDBBtn color="cyan" type="button" onClick={(event)=> this.buttonSubmit(event)}
                            disables={this.buttonSubmit}
                        >
                          Login
                        </MDBBtn>
                      </div>
                    </form>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
        )
    }
}
const mapStateToProps = state => {

       return {
           state: state,
       };
   }
   export default connect(mapStateToProps, { sigIn })(Login);
