import { LOGIN_SUCESS, ERROR_MESSAGE } from '../action/actionType';


const initialState = {
    login: null

    };



    export default function loginReducer (state = initialState, action){
        console.log("reducer", action.payload)
        switch(action.type){
            case LOGIN_SUCESS:
                if ( action.payload && action.payload.data ) {
                    return {...state, login: action.payload.data }
                }
            // case ERROR_MESSAGE:
            // return {}
                return state;

            default:
                return state;
        }

    }
