import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn, MDBCard, MDBCardBody } from 'mdbreact';
import './login.css';   

class Login extends React.Component{
    constructor(props){
        super(props)
        this.state={
            username:'',
            password:'',
        }
    }

    inputChnage = (event) =>{
        const { name, value } =  event.target;


    }
    render(){
        let { username, password } =  this.state;
        return(
            <MDBContainer>
            <MDBRow>
              <MDBCol md="6">
                <MDBCard>
                  <MDBCardBody>
                    <form>
                      <p className="h4 text-center py-4">Login</p>
                      <div className="grey-text">
                        <MDBInput
                         name="username"
                         value={username}
                          label="Your name"
                          icon="user"
                          group
                          type="text"
                          validate
                          error="wrong"
                          success="right"
                        />
                        <MDBInput
                            name="password"
                            value={password}
                          label="Your password"
                          icon="lock"
                          group
                          type="password"
                          validate
                        />
                      </div>
                      <div className="text-center py-4 mt-3">
                        <MDBBtn color="cyan" type="submit">
                          Login
                        </MDBBtn>
                      </div>
                    </form>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
        )
    }
}

export default Login;
