import { LOGIN_SUCESS } from './actionType';
import { login } from '../json/index'

export const sigIn = (data) =>dispatch=>{
    let [ { username, password } ] = login;
    if(username === data.username && password === data.password){
        console.log('true')
        return true
    }else{
        console.log('false')
        return false;
    }

}
